import styled from "styled-components";

export const HeadingStyle = styled.h1`
  text-align: center;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 10px;
  text-transform: uppercase;
  font-size: 4rem;
`;

export const LogoStyle = styled.img`
  width: 5rem;
`;
